mapper.select:
SELECT appointment_id, provider_id, user_id, start_date_time, end_date_time, status, service_type, comments
FROM appointments
WHERE appointment_id = #{id}

mapper.update:
UPDATE appointments
SET status = 'cancelled'
WHERE appointment_id = #{id}

